import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router";
import { Link } from "react-router-dom";
import "./Washpack.css";

function UpdatePack() {
  const [pack, setPack] = useState({
    cost: "",
    packname: "",
    description: "",
  });
  const { id } = useParams();

  const navigate = useNavigate();
  const fetchPackById = () => {
    axios
      .get("http://localhost:8084/admin/viewPack/" + id)
      .then((resp) => setPack(resp.data));
  };
  useEffect(fetchPackById, []);
  const handleChange = (event) => {
    event.persist();
    setPack((pack) => ({ ...pack, [event.target.name]: event.target.value }));
  };
  const handleSubmit = () => {
    const palyload = {
      id: pack.id,
      packname: pack.packname,
      description: pack.description,
      cost: pack.cost,
    };
    axios
      .put("http://localhost:8084/admin/washpack/update", palyload)
      .then((resp) =>
        alert(" id: " + resp.data.id + " updated successfully........")
      );
    navigate(-1);
  };
  return (
    <div className="container" >
     
      <div>
        <h3>
          <h2 className='title' > Update Washpack</h2>
        </h3>
      </div>

      <h2 style={{ color: "green" }}>
        <u>Details</u>
      </h2>
      <div>
        <label style={{ color: "white" }}> ID:</label>
        <input
          type="text"
          className="form-control form-control-sm"
          name="id"
          value={pack.id}
          onChange={handleChange}
          style={{ fontWeight: "700" }}
          disabled
        />
      </div>
      <div>
        <label style={{ color: "white" }}>PackName:</label>
        <input
          type="text"
          className="form-control form-control-sm"
          name="packname"
          value={pack.packname}
          onChange={handleChange}
          style={{ fontWeight: "700" }}
        />
      </div>

      <div>
        <label style={{ color: "white" }}>Description:</label>
        <input
          type="text"
          className="form-control form-control-sm"
          name="cdescription"
          value={pack.description}
          onChange={handleChange}
          style={{ fontWeight: "700" }}
        />
      </div>
      <div>
        <label style={{ color: "white" }}>Cost:</label>
        <input
          type="text"
          className="form-control form-control-sm"
          name="cost"
          value={pack.cost}
          onChange={handleChange}
          style={{ fontWeight: "700" }}
        />
      </div>

      <div>
        <button className="btn btn-primary mb-2" onClick={handleSubmit}>
          Update
        </button>
      </div>
    </div>

  );
}
export default UpdatePack;
