import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./login.css";
import Signup from "./SignUp";
import { Link } from "react-router-dom";
//import { createButton } from "react-social-login-buttons";


const Login = () => {
  let navigate = useNavigate();
  var auth = JSON.stringify(localStorage.getItem("auth"))
  console.log("this is auth string :" + auth);

  // const nav = useNavigate();

  const [formErrors, setFormErrors] = useState({});

  const [input, setInput] = useState({
    username: "",
    password: ""
  });

  const inputEvent = (event) => {
    const { name, value } = event.target;

    setInput((previousvalue) => {
      console.log(previousvalue.data);
      return {
        ...previousvalue,
        [name]: value,
      }
    });
  };
  const showdata = (event) => {
    event.preventDefault();
    let errors = {};

    if (!input.username) {
      errors["usernameErr"] = "username is required";
    }
    if (!input.password) {
      errors["passwordErr"] = "password is required";
    }

    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      const payload = {
        username: input.username,
        password: input.password
      };

      try {
        axios.post("http://localhost:8083/user/authenticate", payload)
          .then((resp) =>
            alert("Welcome" + resp.data.username)
          );

      }
      catch (error) {
        console.log("error is", error)
      };
    }
  }

  return (
    
    <div className="userbg">
    <div>
       <h1 className="title1a">USER LOGIN</h1>  
      <div className="login-form">
        <div className="input-container">
          <label>Username</label>
          <input
            type="text"
            placeholder="  Enter Username"
            value={input.username}
            onChange={inputEvent}
          />
          {formErrors.usernameErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.usernameErr}
                </div>
              )}
          <label>Password</label>
          <input
            type="password"
            placeholder="password"
            value={input.password}
            onChange={inputEvent}
          />
          {formErrors.passwordErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.passwordErr}
                </div>
              )}
            </div>
        <button className="Btn" onClick={{showdata}}>
          Submit
        </button>
        <button
          className="Btn"
          onClick={() => {
            navigate("/Signup");
          }}
        >
          SignUp
        </button>
        <div className="mrginlistalltrainer" style={{fontSize:"30Px",marginLeft:"40px", marginTop: "40px"}}> <a class="previous">&laquo;</a>

             <Link to="/"  style={{color:"chocolate"}} className="previous round">Back</Link> </div>

            <div className='col-10 col-md-8 col-lg-6'style={{  marginTop: "60px" }}></div>
      </div>
      </div>
</div>
  )
        };
export default Login;
