// import { toBeEmpty } from "@testing-library/jest-dom";
import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
// import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./booking.css";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";

function Booking() {
  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    address: "",
    cost: ""


  });

  const navigate = useNavigate();

  const [formErrors, setFormErrors] = useState({});

  const handleChange = (event) => {
    setOrder((order) => ({
      ...order,
      [event.target.name]: event.target.value,
    }));
  };
  const handleSubmit = () => {
    //validate add student form
    let errors = {};
    // const isValid=isValid();
    if (!order.orderId) {
      errors["idErr"] = "Id is required";
    }
    if (!order.carName) {
      errors["carNameErr"] = "carName is required";
    }
    if (!order.carModel) {
      errors["carModelErr"] = "carModel is required";
    }

    if (!order.contactno) {
      errors["contactnoErr"] = "contact no is required";
    }
    if (!order.date) {
      errors["dateErr"] = "date is required";
    }
    if (!order.cost) {
      errors["costErr"] = "cost is required";
    }
    if (!order.username) {
      errors["usernameErr"] = "username is required";
    }
    if (!order.address) {
      errors["addressErr"] = "address is required";
    }
    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      const payload = {
        carModel: order.carModel,
        carName: order.carName,
        contactno: order.contactno,
        date: order.date,
        orderId: order.orderId,
        username: order.username,
        address: order.address,
        cost: order.cost
      };
      axios
        .post(
          " http://localhost:8081/order/addorder",
          payload
        )
        .then((resp) =>
          alert("Order is saved with id:" + resp.data.orderId)
        );
    }
  };

  return (
    <>
      <div className="formTop">
        <div className="formTitle">
          <p className="c1">
            <h1>BOOK YOUR CAR WASH </h1>
          </p>
        </div>
      </div>
      <div>
        <div className="formDetails">
          <form className="actualForm" onSubmit={handleChange}>
            <div style={{ marginBottom: "30px" }}>
              <p className="formName">FILL BELOW DETAILS</p>
            </div>
            <div className="formDateTime">
              <input type="date" name="date" onChange={handleSubmit} />
              {formErrors.idErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.idErr}
                </div>
              )}
            </div>

            {order.map((emp, ind) => (
              <div style={{ marginBottom: "20px" }}>
                <option>{emp.packname}</option>
                <input type="radio" onChange={handleSubmit} />
                {emp.cost}
              </div>
            ))}

            <div style={{ marginBottom: "30px" }}>
              <p className="formName">CONTACT DETAILS</p>
            </div>
            <div>
              <input
                className="contactDetails"
                type="text"
                name="username"
                onChange={handleSubmit}
                placeholder="Enter Your Full Name"
              />
              {formErrors.usernameErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.usernameErr}
                </div>
              )}
              <br />
              <input
                className="contactDetails"
                name="carName"
                type="text"
                placeholder="Enter Your Car Name"
                onChange={handleSubmit}
              />
              {formErrors.carNameErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.carNameErr}
                </div>
              )}
              <br />

              <input
                className="contactDetails"
                type="text"
                name="carModel"
                placeholder="Enter Your Car Model"
                onChange={handleSubmit}
              />
              {formErrors.carModelErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.carModelErr}
                </div>
              )}
              <br />
              <input
                className="contactDetails"
                type="text"
                name="cost"
                onChange={handleSubmit}
                placeholder="Enter washpack cost"
              />
              <br />
              <input
                type="text"
                className="contactDetails"
                name="contactno"
                onChange={handleSubmit}
                placeholder="Enter Phone"
              />
              {formErrors.contactnoErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.contactnoErr}
                </div>
              )}
              <br />

              <textarea
                cols="30"
                className="contactDetailsTextArea"
                rows="10"
                type="text"
                name="address"
                onChange={handleSubmit}
                placeholder="Your Address"
              />
              {formErrors.addressErr && (
                <div style={{ color: "red", paddingBottom: 10 }}>
                  {formErrors.addressErr}
                </div>
              )}
            </div>
            <div className="Submit">
              <button
                type="submit"
                className="btn btn-success"
                onClick={() => {
                  handleChange();
                  navigate("/Payment");
                }}
              >
                Submit
              </button>

            </div>
          </form>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </>
  );
};
export default Booking;
