import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router';
import { useNavigate } from 'react-router-dom';
// import { baseUrl } from '../util/AppConstants';
function DeleteOrder() {
    const [order, setOrder] = useState(null)
    const { id } = useParams();
    const navigate = useNavigate();
    const fetchOrderById = () => {
        axios.get("http://localhost:8081/order/viewOrder/"+id).then(resp => setOrder(resp.data))
    }
    useEffect(fetchOrderById);
    const deleteOrder = () => {
        axios.delete("http://localhost:8081/order/delete/" + id).then(resp => {
            alert(resp.data);
           
        })
      




    }
    return (
        <div>
            <h2>Order Details</h2>
            {
                order !== null &&
                <div >
                    
                    <p> Order ID : {order.orderId}</p>
                    <p> CARNAME : {order.carModel}</p>
                    <p> USERNAME : {order.username}</p>
                    <p> DATE : {order.date}</p>
                    <p> CONTACT : {order.contactno}</p>
                    <p> ADDRESS : {order.address}</p>
                
                </div>
            }
            <div><button onClick={deleteOrder}>Delete</button></div>
        </div>
    )
}
export default DeleteOrder;
