// import { toBeEmpty } from "@testing-library/jest-dom";
import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
// import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./booking.css";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";

function Booking() {
  // const [washers, setWashers] = useState([]);

  // useEffect(() => {
  //   fetch("http://localhost:8084/admin/washpack").then((result) => {
  //     result.json().then((resp) => {
  //       setWashers(resp);
  //       console.log(resp);
  //     });
  //   });
  // }, []);

  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    address: "",
    cost: ""


  });

  const navigate = useNavigate();

  const [formErrors, setFormErrors] = useState({});

  const handleChange = (event) => {
    setOrder((order) => ({
      ...order,
      [event.target.name]: event.target.value,
    }));
  };
  const handleSubmit = () => {
    //validate add student form
    let errors = {};
    // const isValid=isValid();
    if (!order.orderId) {
      errors["idErr"] = "Id is required";
    }
    if (!order.carName) {
      errors["carNameErr"] = "carName is required";
    }
    if (!order.carModel) {
      errors["carModelErr"] = "carModel is required";
    }

    if (!order.contactno) {
      errors["contactnoErr"] = "contact no is required";
    }
    if (!order.date) {
      errors["dateErr"] = "date is required";
    }
    if (!order.cost) {
      errors["costErr"] = "cost is required";
    }
    if (!order.username) {
      errors["usernameErr"] = "username is required";
    }
    if (!order.address) {
      errors["addressErr"] = "address is required";
    }
    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      const payload = {
        carModel: order.carModel,
        carName: order.carName,
        contactno: order.contactno,
        date: order.date,
        orderId: order.orderId,
        username: order.username,
        address: order.address,
        cost: order.cost
      };
      axios
        .post(
          " http://localhost:8081/order/addorder",
          payload
        )
        .then((resp) =>
          alert("Order is saved with id:" + resp.data.orderId)
        );
    }
  };
  return (
    <div className="form" style={{marginTop:"7%"}}>

         
     <div  style={{fontSize:"20px"}}className="mrginlistalltrainer"> <a class="previous">&laquo;</a> 
      <Link to="/student/login"  style={{color:"chocolate"}} className="previous round"><u>Back</u></Link> </div>
    
       
      <div className="form-body">
        <Header />
        <div className="username">
          <label className="form__label" for="firstName">
            First Name
          </label>
          <input
            className="form__input"
            id="fname"
            // className="form-control"
            type="text"
            name="firstName"
            placeholder="First Name"
            value={student.firstName}
            onChange={handleChange}
          />
          {formErrors.firstNameErr && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.firstNameErr}
            </div>
          )}
        </div>
        <div className="lastname">
          <label className="form__label" for="lastName">
            Last Name
          </label>
          <input
            id="lname"
            className="form__input"
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={student.lastName}
            onChange={handleChange}
          />
          {formErrors.lastNameErr && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.lastNameErr}
            </div>
          )}
        </div>

        <div className="email">
          <label className="form__label" for="email">
            Email ID
          </label>
          <input
            id="emailid"
            className="form__input"
            type="email"
            name="emailId"
            placeholder="Email Id"
            value={student.emailId}
            onChange={handleChange}
          />
          {formErrors.emailIdErr && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.emailIdErr}
            </div>
          )}
        </div>
        <div className="contact">
          <label className="form__label" for="contact">
            Contact
          </label>
          <input
            id="contactnumber"
            className="form__input"
            type="number"
            name="contactNumber"
            placeholder="Mobile Number"
            value={student.contactNumber}
            onChange={handleChange}
          />
          {formErrors.contactNumberErr && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.contactNumberErr}
            </div>
          )}
        </div>
        <div className="username">
          <label className="form__label" for="userName">
            User Name
          </label>
          <input
            id="username"
            className="form__input"
            type="text"
            name="userName"
            placeholder="User Name"
            value={student.userName}
            onChange={handleChange}
          />
          {formErrors.userNameErr && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.userNameErr}
            </div>
          )}
        </div>

        <div className="password">
          <label className="form__label" for="password">
            Password
          </label>
          <input
            id="password"
            className="form__input"
            type="password"
            name="password"
            placeholder="PassWord"
            value={student.password}
            onChange={handleChange}
          />
          {formErrors.passwordErr && (
            <div style={{ color: "red", paddingBottom: 10 }}>
              {formErrors.passwordErr}
            </div>
          )}
        </div>
      </div>
      <div class="footer">
        <button
          type="submit"
          class="btn"
          style={{ backgroundColor: "blue" }}
          onClick={handleSubmit}
        >
          Register
        </button>
      </div>
    </div>
  );
}
export default Booking;
