import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Table } from "react-bootstrap";
import Footer from "./Footer";

export default class Profile extends Component { // Start
  // Constructor
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false,
    };
  }

  // ComponentDidMount is used to
  // execute the code
  componentDidMount() {
    fetch("http://localhost:8083/user/allusers")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true,
        });
      });
  }

  //End

  render() {
    const { DataisLoaded, items } = this.state;

    return (

           <div>
            <h4  style={{ color: "red", backgroundColor:"powderblue", fontSize:"30px"}} className = "text-center">List Of Users</h4>
<hr/>
<table  className="table table-bordered table-striped">

<thead  class="thead-dark">

{/* class="black white-text" */}

  <tr>

  <th>Name</th>
 <th>Username</th>
<th>Email</th>
    <th>Contact</th>
  </tr>

</thead>

<tbody>

          {items.map((item, index) => (

            <tr  class="table-info" key={index}>

<td>{item.name}</td>
                  <td>{item.username}</td>
                  <td>{item.email}</td>
                  <td>{item.contactno}</td>
              </tr>

          ))}

        </tbody>

      </table>


        <div>
          <Footer />
        </div>
        </div>
    );
  }
}
