//import { toBeEmpty } from "@testing-library/jest-dom";
import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./booking.css";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";

toast.configure();
const Booking = () => {
  // Start
  const [washers, setWashers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8084/admin/washpack").then((result) => {
      result.json().then((resp) => {
        setWashers(resp);
        console.log(resp);
      });
    });
  }, []);

  // End
  let navigate = useNavigate();

  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    address: "",
    cost:""
  });
  const [formErrors, setFormErrors] = useState({});
  const changeHandler = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const submitHandler = () => {
    // axios
    //   .post("http://localhost:8081/order/addorder", order)
    //   .then((res) => {
      //   if (res.status === 200) alert("Booked Successfully");
      //   else Promise.reject();
      // })
      // .catch((err) => alert("Something went wrong"));
      let errors = {};
      if (!order.orderId) {
        errors["idErr"] = "Id is required";
      }
      if (!order.carName) {
        errors["carNameErr"] = "carName is required";
      }
      if (!order.carModel) {
        errors["carModelErr"] = "carModel is required";
      }
  
      if (!order.contactno) {
        errors["contactnoErr"] = "contact no is required";
      }
      if (!order.date) {
        errors["dateErr"] = "date is required";
      }
      if (!order.cost) {
        errors["costErr"] = "cost is required";
      }
      if (!order.username) {
        errors["usernameErr"] = "username is required";
      }
      if (!order.address) {
        errors["addressErr"] = "address is required";
      }
      setFormErrors(errors);
      const noErrors = Object.keys(errors).length === 0;
      if (noErrors) {
        const payload = {
          carModel: order.carModel,
          carName: order.carName,
          contactno: order.contactno,
          date: order.date,
          orderId: order.orderId,
          username: order.username,
          address: order.address,
          cost: order.cost
        };
        axios
          .post(
            " http://localhost:8081/order/addorder",
            payload
          )
          .then((resp) =>
          alert("Order is saved with id:" + resp.data.orderId)
        );
      }

    }
  return (
    <>
      <div className="formTop">
        <div className="formTitle">
          <p className="c1">
            <h1>BOOK YOUR CAR WASH </h1>
          </p>
        </div>
      </div>
      <div>
        <div className="formDetails">
          <form className="actualForm" onSubmit={submitHandler}>
            <div style={{ marginBottom: "30px" }}>
              <p className="formName">FILL BELOW DETAILS</p>
            </div>
            <div className="formDateTime">
              <h3>Date for Booking</h3>
              <input type="date" name="date" onChange={changeHandler} />
            </div>

            {washers.map((emp, ind) => (
              <div style={{ marginBottom: "20px" }}>
                <option>{emp.packname}</option>
                <input type="radio" onChange={changeHandler} /> 
                {emp.cost}
              </div>
            ))}

            <div style={{ marginBottom: "30px" }}>
              <p className="formName">CONTACT DETAILS</p>
            </div>
            <div>
              <input
                className="contactDetails"
                type="text"
                name="username"
                onChange={changeHandler}
                placeholder="Enter Your Full Name"
              />
              <br />
              <input
                className="contactDetails"
                name="carName"
                type="text"
                placeholder="Enter Your Car Name"
                onChange={changeHandler}
              />
              <br />

              <input
                className="contactDetails"
                type="text"
                name="carModel"
                placeholder="Enter Your Car Model"
                onChange={changeHandler}
              />
              <br />
              <input
                className="contactDetails"
                type="text"
                name="cost"
                onChange={changeHandler}
                placeholder="Enter washpack cost"
              />
              <br />
              <input
                type="text"
                className="contactDetails"
                name="contactno"
                onChange={changeHandler}
                placeholder="Enter Phone"
              />
              <br />

              <textarea
                cols="30"
                className="contactDetailsTextArea"
                rows="10"
                type="text"
                name="address"
                onChange={changeHandler}
                placeholder="Your Address"
              />
            </div>
            <div className="Submit">
              <button
                type="submit"
                className="btn btn-success"
                onClick={() => {submitHandler();navigate("/Payment"); }}
             >
                Submit
             </button>
               
            </div>
          </form>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </>
  );
};
export default Booking;
