import React, { useState } from 'react';
import axios from 'axios';
import "./Washpack.css";
//import {  toast } from 'react-toastify';


function Addpacks() {
  const [pack, setPack] = useState({
    id: 0,
    packname: "",
     
    description: "",
    cost: ""
   
      
  });
  const [formErrors, setFormErrors] = useState({});
  const handleChange = (event) => {
    event.persist();
    setPack((pack) => ({
    ...pack,
      [event.target.name]: event.target.value,
      
    }));
  };
  const handleSubmit = () => {
    //validate add trainer form
    let errors = {};
    if (!pack.id){
        errors["idErr"]="id is required";
    }
    if (!pack.packname) {
      errors["packNameErr"] = "packName is required";
    }
    
    if (!pack.description) {
      errors["descriptionErr"] = "description is required";
    }
    if (!pack.cost){
        errors["costErr"]="cost is required";
    }
    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      const payload = {
          id:pack.id,
        packname: pack.packname,
        description: pack.description,
        cost:pack.cost,
      };
      axios
        .post(
         " http://localhost:8084/admin/addwashpack",
          payload
        )
        .then((resp) =>
          alert("pack is saved with id:" + resp.data.id)
        );
    }
  };
  return (
     <div className='in'>
        <div className='container' style={{width:"100px"}}>
          <h2 className='title'  > Add packs</h2>
        <label style={{color: "white"}}> Id</label>
        <input
        id="id"
         className="form-control"
          type="number"
          name="id"
          value={pack.id}
          onChange={handleChange}
        />
        {formErrors.idErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.idErr}
          </div>
        )}
      
     <br></br>
      
        <label style={{color: "white"}}>Pack Name</label>
        <input
        id="pname"
         className="form-control"
          type="text"
          name="packname"
          value={pack.packname}
          onChange={handleChange}
        />
         {formErrors.packNameErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.packNameErr}
          </div>
        )}
      
      <br></br>
      
        <label style={{color: "white"}}>Description</label>
        <input
        id="description"
         className="form-control"
          type="text"
          name="description"
          value={pack.description}
          onChange={handleChange}
        />
         {formErrors.descriptionErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.descriptionErr}
          </div>
        )}
        <br></br>
     
        <label style={{color: "white"}}>Cost</label>
        <input
        id="cost"
         className="form-control"
          type="number"
          name="cost"
          value={pack.cost}
          onChange={handleChange}
        />
         {formErrors.costErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.costErr}
          </div>
        )}
      <br></br>

      <div >
            <button className="btn btn-outline-light btn-lg px-5" type="submit" onClick={handleSubmit } >Submit</button>
            </div>
    </div>
    </div>
  );
}

export default Addpacks;
