import React, { useState } from 'react';
import axios from 'axios';

//import {  toast } from 'react-toastify';


function AddPacks() {
  const [pack, setPack] = useState({
    cost: 0,
  description: "",
  packname: ""
  });
   
  const navigate = useNavigate();
  const [formErrors, setFormErrors] = useState({});
  const handleChange = (event) => {
    setPack((pack) => ({
    ...pack,
      [event.target.name]: event.target.value,
      //[event.target.name]: event.target.type === 'String' ? parseInt(event.target.value) : event.target.value,
    }));
  };
  const handleSubmit = () => {
    //validate add trainer form
    let errors = {};
    if (!pack.cost) {
      errors["firstNameErr"] = "cost is required";
    }
    
    if (!pack.description) {
      errors["lastNameErr"] = "description is required";
    }
    if (!pack.packname) {
      errors["packageNameErr"] = "packname is required";
    }
    setFormErrors(errors);
    const noErrors = Object.keys(errors).length === 0;
    if (noErrors) {
      const payload = {
        cost: pack.cost,
        description: pack.description,
        packname: pack.packname,

      };
      axios
        .post(
          "http://localhost:8084/admin/addwashpack",
          payload
        )
        .then((resp) =>
          alert("washpack is saved with id:" + resp.data.id)
        );
        navigate(-1);
    }
  };
  return (
    <div  className="trainerbgpg" style={{marginTop:"7%"}}>
      <NavBarAdmin/>

<div className="mrginlistalltrainer" style={{fontsize:"20px"}}> <a class="previous">&laquo;</a> 
<Link to="/"  style={{color:"chocolte"}} className="previous round">Back</Link> </div>
      <div  className="addingtrainerpage">
        <label>Packname</label>
        <input
        id="pname"
         className="form-control"
          type="text"
          name="Package"
          value={pack.packname}
          onChange={handleChange}
        />
        {formErrors.packageNameErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.packageNameErr}
          </div>
        )}
      </div>
     
      <div>
        <label>Cost</label>
        <input
        id="cname"
         className="form-control"
          type="number"
          name="cost"
          value={pack.cost}
          onChange={handleChange}
        />
         {formErrors.firstNameErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.firstNameErr}
          </div>
        )}
      </div>
      <div>
        <label>Description</label>
        <input
        id="dname"
         className="form-control"
          type="text"
          name="desc"
          value={pack.description}
          onChange={handleChange}
        />
         {formErrors.lastNameErr && (
          <div style={{ color: "red", paddingBottom: 10 }}>
            {formErrors.lastNameErr}
          </div>
        )}
      </div>

      <div id="outer" >
        <div  class="inner1"><button className = "btn btn-primary mb-2"  onClick={handleSubmit}>Save</button></div>
        {/* <div class="inner2"><Link to="/getAllTrainers" className="btn btn-danger"> Cancel </Link></div> */}
      </div>
    </div>
  );
}
export default AddPacks;
